import React, {Component} from 'react'; 
import styled, { css } from 'styled-components';
import '../components/marquee.css';
import marq from './images/testmark@2x.png';


const Header = styled.div`
    position: fixed;
    top: 0;
    left: 0;
    width: 100vw;
    font-family: Adieu, sans-serif;
    font-size: 1.4rem;
    color: white;
    border-bottom: 2px solid white;
    overflow: hidden

    // @media (max-width: 1000px) {
    //    margin-left: 7vw;
    //    margin-right: 7vw;

    //   }

    //   @media (max-width: 500px) {
    //   font-size: 18px;
    //   line-height: 1.8;

    //    }
`;


const MarqueeImg = styled.div`
    width: 200vw;
    min-width: 8330px;
    height: 2rem;
    background: url(${marq}) 0 center repeat-x red;
    background-size: auto 2rem;
    -webkit-transform: translateX(0);
    transform: translateX(0);
    -webkit-animation: Marquee-img 60s linear infinite;
    animation: Marquee-img 60s linear infinite

    // @media (max-width: 1000px) {
    //    margin-left: 7vw;
    //    margin-right: 7vw;

    //   }

    //   @media (max-width: 500px) {
    //   font-size: 18px;
    //   line-height: 1.8;

    //    }
`;



const Marquee = ( {text, color} ) => {
  return(
    <React.Fragment> 
        <Header > </Header>
        <MarqueeImg></MarqueeImg>
    </React.Fragment>
  )
}

export default Marquee;
