import React, { useState, useEffect } from 'react';

const Colleges = () => {
  const [colleges, setColleges] = useState(['STANFORD','USC','YALE','BROWN','UCLA','BERKELEY','CORNELL','MIT','PENN','HARVARD','PRINCETON','DARTMOUTH']);
  const [timer, setTimer] = useState(0);
  const [isValid, setValid] = useState(false);


//  const [colleges, setColleges] = useState([]);
//  useEffect(() => {
//     setColleges(['STANFORD','USC','YALE','BROWN','UCLA','BERKELEY','CORNELL','MIT','PENN','HARVARD','PRINCETON','DARTMOUTH']);

//  })

    useEffect(() => {
        const interval = setInterval(() => {
        setColleges(colleges.pop());
        }, 1000)

        return () => clearInterval(interval);
    }, []);


    useEffect(() => {
        const interval = setInterval(() => {
          setTimer(timer => timer + 1);
        }, 1000)
        if (timer >1){
            setValid(true);
        }
        return () => clearInterval(interval);
    }, []);



  return (
    <div className="App">
      <header className="App-header">
      </header>
      {isValid? 
            <div>{colleges} FAXED</div>
      :  <div>YO </div> }

      {/* <div> {timer} seconds</div> */}
    </div>
  );
};

export default Colleges;
