import React, {Component} from 'react'; 
import styled, { css } from 'styled-components'
import './image.css';

const Wrapper = styled.div`
display: flex;
flex-direction: row;
margin-left: 20%;
margin-right: 20%;
margin-top: 1.5em;
margin-bottom: 2em;

@media (max-width: 800px) {
    margin-left: 7vw;
    margin-right: 7vw;
   }

   @media (max-width: 500px) {
    font-size: 24px;
    line-height: 1.8;

     }
`;


const SplitImage = ( {image1,image2, caption, color} ) => {
  return(
      <Wrapper> 
      <img className = "small-img1" src ={require(`${image1}`)} /> 

      <img className = "small-img2" src ={require(`${image1}`)} /> 
      </Wrapper>
  )
}

export default SplitImage;
