class AddOpp extends Component{

    constructor(props){
        super(props);
            this.state = {
                name: "",
                new_count: false
            };
        }
    changeHandler = e => {
        this.setState({name: e.target.value});
    }

    submitHandler = e => {
        e.preventDefault()
        console.log("should input test data" +this.state)
        axios.post('https://l2wmf923e0.execute-api.us-west-1.amazonaws.com/fns/fax')
